parameters.query:
let row = dynamic({Operation});
let operation = tostring(row.Operation);
let app = tostring(row.App);
range i from 1 to 1 step 1
| project Operation = iff((operation == '' and app == '') or (operation == 'üî∏ All operations' and app == 'üî∏ All Apps'), 'all operations', operation)

content.query:
let row = dynamic({Operation});
let operation = tostring(row.Operation);
let app = tostring(row.App);
requests
| where timestamp {TimeRange}
| where (name == operation and appName == app) or (operation == '' and app == '') or (operation == 'üî∏ All operations' and app == 'üî∏ All Apps')
| make-series Duration = {Show} default = 0 on timestamp in range({TimeRange:start}, {TimeRange:end}, {TimeRange:grain})
| mvexpand timestamp to typeof(datetime), Duration to typeof(long)

